    private ChineseST() {
    }

    /**
     * 将简体汉字转换成为繁体汉字
     * @param chinese 简体汉字
     * @return 繁体汉字
     */
    public static String toTranditional(String chinese) {
        if(chinese == null || chinese.length() == 0) {
            return chinese;
        }
        char[] chs = chinese.toCharArray();
        for(int i = 0; i < chs.length; i++) {
            if(!isChinese(chs[i])) {
                continue;
            }
            int index = findSimpleIndex(chs[i]);
            if(index > -1) {
                chs[i] = ST_TRANDITIONAL_CHINESE[index];
            }
        }
        return new String(chs);
    }

    /**
     * 将繁体汉字转换成为简体汉字
     * @param chinese 繁体汉字
     * @return 简体汉字
     */
    public static String toSimple(String chinese) {
        if(chinese == null || chinese.length() == 0) {
            return chinese;
        }
        char[] chs = chinese.toCharArray();
        for(int i = 0; i < chs.length; i++) {
            if(!isChinese(chs[i])) {
                continue;
            }
            int index = findTranditionalIndex(chs[i]);
            if(index > -1) {
                chs[i] = TS_SIMPLE_CHINESE[index];
            }
        }
        return new String(chs);
    }

    /**
     * 测试当前字符是否是汉字
     * @param c 字符
     * @return 当前字符是否是汉字
     */
    public static boolean isChinese(char c) {
        return (c >= 0x4e00 && c <= 0x9fff);
    }

    private static int findSimpleIndex(char c) {
        return findIndex(ST_SIMPLE_CHINESE, c);
    }

    private static int findTranditionalIndex(char c) {
        return findIndex(TS_TRANDITIONAL_CHINESE, c);
    }

    private static int findIndex(char[] chs, char c) {
        return Arrays.binarySearch(chs, c);
    }
